mapper.delete:
delete from tbl_cart
    where cartNum = #{cartNum}
        and userId = #{userId}

mapper.select:
 select
    orderId, userId, gdsName, gdsPrice, gdsStock , amount
from tbl_order 
    where userId = #{userId}
     and orderId = #{orderId}

mapper.delete:
delete from tbl_cart
	 where cartNum = #{cartNum}
	 and userId = #{userId}

delete from tbl_cart
	 where userId = #{userId} and
	 
	 	cartNum = #{cartNum}
